// src/pages/login.js
import { useState } from 'react';

const API_BASE = process.env.REACT_APP_API_BASE_URL_LOGIN;

export default function Login() {
  const [email, setEmail]   = useState('');
  const [pass, setPass]     = useState('');
  const [error, setError]   = useState('');

  const onSubmit = async e => {
    e.preventDefault();
    setError('');

    try {
      const resp = await fetch(
        `${API_BASE}/auth/token`,
        {
          method: 'POST',
          mode: 'cors',                   // cross-origin
          credentials: 'include',         // include HttpOnly cookies
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            username: email,
            password: pass
          })
        }
      );
      console.log('Response status:', resp.status);

      if (!resp.ok) {
        const errText = await resp.text();
        throw new Error(errText || 'Unknown error');
      }

      // on success, cookies have been set by your Lambda → just redirect
      window.location.href = '/';
    } catch (err) {
      console.error('Login error:', err);
      setError('Login failed: ' + err.message);
    }
  };

  return (
    <div style={{ maxWidth: 400, margin: '2rem auto' }}>
      <h2>Sign In</h2>
      <form onSubmit={onSubmit}>
        <div>
          <label htmlFor="email">Username (email)</label><br />
          <input
            id="email"
            type="email"
            value={email}
            onChange={e => setEmail(e.target.value)}
            required
            style={{ width: '100%' }}
          />
        </div>
        <div style={{ marginTop: 8 }}>
          <label htmlFor="password">Password</label><br />
          <input
            id="password"
            type="password"
            value={pass}
            onChange={e => setPass(e.target.value)}
            required
            style={{ width: '100%' }}
          />
        </div>
        <button type="submit" style={{ marginTop: 12, padding: '8px 16px' }}>
          Login
        </button>
      </form>
      {error && (
        <p style={{ color: 'red', marginTop: 12 }}>{error}</p>
      )}
    </div>
  );
}
