// src/pages/login.jsx
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';  // or remove if you don't use React Router

export default function Login() {
  const [email, setEmail] = useState('');
  const [pass, setPass]   = useState('');
  const [error, setError] = useState('');

  // set document title
  useEffect(() => {
    document.title = 'Login Page';
  }, []);

  const onSubmit = async e => {
    e.preventDefault();
    setError('');

    try {
      const resp = await fetch(
        'https://1wo0qdba0g.execute-api.eu-north-1.amazonaws.com/dev/login',
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ email, password: pass })
        }
      );

      const data = await resp.json();

      if (resp.ok && data.accessToken && data.idToken) {
        localStorage.setItem('accessToken', data.accessToken);
        localStorage.setItem('idToken',     data.idToken);
        window.location.href = '/tasks';  // or use history.push('/tasks') if you prefer
      } else {
        setError('Invalid login credentials');
      }
    } catch (err) {
      setError('Error: ' + err.message);
    }
  };

  return (
    <>
      <header>
        <div className="logo">Task<span>Manager</span></div>
      </header>

      <main>
        <div className="auth-container">
          <h2 className="auth-title">Welcome Back</h2>
          <div className="card">
            <form onSubmit={onSubmit}>
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  placeholder="Enter your email"
                  value={email}
                  onChange={e => setEmail(e.target.value)}
                  required
                />
              </div>

              <div className="form-group" style={{ marginTop: '1rem' }}>
                <label htmlFor="password">Password</label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  placeholder="Enter your password"
                  value={pass}
                  onChange={e => setPass(e.target.value)}
                  required
                />
              </div>

              <div className="button-group" style={{ marginTop: '1rem' }}>
                <button type="submit">Login</button>
              </div>
            </form>

            {error && (
              <p id="response" style={{ color: 'red', marginTop: '1rem' }}>
                {error}
              </p>
            )}

            <p className="auth-footer" style={{ marginTop: '1rem' }}>
              Don't have an account?{' '}
              <Link to="/signup">Sign Up</Link>
            </p>
          </div>
        </div>
      </main>
    </>
  );
}
