// src/pages/CreateTask.jsx

import React, { useState } from 'react';
import {
  Container, Card, Form, Button, Row, Col, Alert, Spinner
} from 'react-bootstrap';
import api from '../api';
import { useNavigate } from 'react-router-dom';

export default function CreateTask() {
  const navigate = useNavigate();
  const userId   = '32548b14-5f37-4cf1-8928-9a5f59efdfc6';

  const [title,       setTitle]       = useState('');
  const [description, setDescription] = useState('');
  const [priority,    setPriority]    = useState('Medium');
  const [files,       setFiles]       = useState([]);      // File objects
  const [error,       setError]       = useState(null);
  const [saving,      setSaving]      = useState(false);

  // 1) Ask API Gateway for a presigned PUT URL
  async function getPresignedUrl(file) {
    const resp = await api.post(
      '/tasks/attachment-url',
      { filename: file.name, fileType: file.type },
      { headers: { 'Content-Type': 'application/json' } }
    );
    return resp.data; // { uploadUrl, fileUrl }
  }

  // 2) Upload each file to S3 via fetch(PUT)
  async function uploadAllFiles() {
    const urls = [];
    for (let file of files) {
      const { uploadUrl, fileUrl } = await getPresignedUrl(file);

      // PUT the binary file to the presigned URL
      const putResp = await fetch(uploadUrl, {
        method: 'PUT',
        headers: { 'Content-Type': file.type },
        body: file
      });
      if (!putResp.ok) {
        throw new Error(`Upload failed for ${file.name}: ${putResp.status}`);
      }

      urls.push(fileUrl);
    }
    return urls;  // list of public S3 URLs
  }

  const handleSubmit = async e => {
    e.preventDefault();
    setError(null);
    setSaving(true);

    try {
      // upload files first (if any)
      const attachments = files.length
        ? await uploadAllFiles()
        : [];

      // build payload for your createTask Lambda
      const payload = {
        title,
        description,
        status:      'pending',
        priority,
        cognito_sub: userId,
        attachments
      };

      // 3) call the Lambda-backed POST /tasks
      const resp = await api.post(
        '/tasks',
        payload,
        { headers: { 'Content-Type': 'application/json' } }
      );

      if (resp.status === 201) {
        navigate('/tasks');
      } else {
        throw new Error(resp.data?.error || 'Unexpected response');
      }
    } catch (err) {
      console.error(err);
      setError(err.message || 'Failed to create task');
    } finally {
      setSaving(false);
    }
  };

  return (
    <Container className="my-5">
      <Card className="mx-auto shadow-sm" style={{ maxWidth: '600px' }}>
        <Card.Header as="h3" className="bg-primary text-white">
          Create New Task
        </Card.Header>
        <Card.Body>
          {error && <Alert variant="danger">{error}</Alert>}

          <Form onSubmit={handleSubmit}>
            {/* Title */}
            <Form.Group as={Row} className="mb-3" controlId="taskTitle">
              <Form.Label column sm={3}>Title</Form.Label>
              <Col sm={9}>
                <Form.Control
                  type="text"
                  placeholder="Enter task title"
                  value={title}
                  onChange={e => setTitle(e.target.value)}
                  required
                />
              </Col>
            </Form.Group>

            {/* Description */}
            <Form.Group className="mb-3" controlId="taskDescription">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={4}
                placeholder="Enter a description"
                value={description}
                onChange={e => setDescription(e.target.value)}
              />
            </Form.Group>

            {/* Priority */}
            <Form.Group as={Row} className="mb-3" controlId="taskPriority">
              <Form.Label column sm={3}>Priority</Form.Label>
              <Col sm={9}>
                <Form.Select
                  value={priority}
                  onChange={e => setPriority(e.target.value)}
                  required
                >
                  <option value="Low">Low</option>
                  <option value="Medium">Medium</option>
                  <option value="High">High</option>
                </Form.Select>
              </Col>
            </Form.Group>

            {/* Attachments */}
            <Form.Group className="mb-4" controlId="taskAttachments">
              <Form.Label>Attachments</Form.Label>
              <Form.Control
                type="file"
                multiple
                onChange={e => setFiles(Array.from(e.target.files))}
              />
              {files.length > 0 && (
                <ul className="mt-2">
                  {files.map((f, i) => (
                    <li key={i}>{f.name}</li>
                  ))}
                </ul>
              )}
            </Form.Group>

            {/* Submit */}
            <div className="text-end">
              <Button variant="primary" type="submit" disabled={saving}>
                {saving
                  ? <><Spinner animation="border" size="sm" /> Creatingâ€¦</>
                  : 'Create Task'}
              </Button>
            </div>
          </Form>
        </Card.Body>
      </Card>
    </Container>
  );
}
