// src/pages/CreateTask.jsx

import React, { useState } from 'react'
import {
  Container, Card, Form, Button,
  Row, Col, Alert, Spinner
} from 'react-bootstrap'
import api from '../api'
import { useNavigate } from 'react-router-dom'
import Cookies from 'js-cookie';
import jwt_decode from 'jwt-decode';

export default function CreateTask() {
  const navigate = useNavigate()
useEffect(() => {
    const extractUserId = () => {
      try {
        const idToken = Cookies.get('idToken');
        if (!idToken) {
          throw new Error('No authentication token found');
        }

        const decoded = jwt_decode(idToken);
        if (!decoded.sub) {
          throw new Error('User ID not found in token');
        }

        setUserId(decoded.sub);
        setLoadingUser(false);
      } catch (err) {
        console.error('Error extracting user ID:', err);
        setError('Authentication error. Please login again.');
        setLoadingUser(false);
        // Optionally redirect to login
        // navigate('/login');
      }
    };

    extractUserId();
  }, []);

  const [title,       setTitle]       = useState('')
  const [description, setDescription] = useState('')
  const [dueDate,     setDueDate]     = useState('')
  const [priority,    setPriority]    = useState('Medium')
  const [files,       setFiles]       = useState([])     // File objects
  const [error,       setError]       = useState(null)
  const [saving,      setSaving]      = useState(false)

  // helper: read a File into a base64 string
  function fileToBase64(file) {
    return new Promise((res, rej) => {
      const reader = new FileReader()
      reader.onload = () => res(reader.result.split(',')[1])  // drop the "data:*/*;base64," prefix
      reader.onerror = e => rej(e)
      reader.readAsDataURL(file)
    })
  }

  const handleSubmit = async e => {
    e.preventDefault()
    setError(null)
    setSaving(true)

    try {
      // 1) turn each File into { filename, fileType, data: base64 }
      const attachments = await Promise.all(
        files.map(async f => ({
          filename: f.name,
          fileType: f.type,
          data:     await fileToBase64(f)
        }))
      )

      // 2) build a pure-JSON payload
      const payload = {
        title,
        description,
        dueDate,
        priority,
        userId,
        attachments        // may be []
      }

      // 3) send JSON to your Lambda-backed POST /tasks
      const resp = await api.post(
        '/tasks',
        payload,
        { headers: { 'Content-Type': 'application/json' } }
      )

      if (resp.status === 201) {
        navigate('/tasks')
      } else {
        throw new Error(resp.data?.error || 'Unexpected response')
      }
    } catch (err) {
      console.error(err)
      setError(err.message || 'Create failed')
    } finally {
      setSaving(false)
    }
  }

  return (
    <Container className="my-5">
      <Card className="mx-auto shadow-sm" style={{maxWidth:600}}>
        <Card.Header className="bg-primary text-white">
          <h3>Create New Task</h3>
        </Card.Header>
        <Card.Body>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={handleSubmit}>

            <Form.Group as={Row} className="mb-3">
              <Form.Label column sm={3}>Title</Form.Label>
              <Col sm={9}>
                <Form.Control
                  value={title}
                  onChange={e => setTitle(e.target.value)}
                  required
                />
              </Col>
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea" rows={4}
                value={description}
                onChange={e => setDescription(e.target.value)}
              />
            </Form.Group>

            <Form.Group as={Row} className="mb-3">
              <Form.Label column sm={3}>Priority</Form.Label>
              <Col sm={9}>
                <Form.Select
                  value={priority}
                  onChange={e => setPriority(e.target.value)}
                >
                  <option>Low</option>
                  <option>Medium</option>
                  <option>High</option>
                </Form.Select>
              </Col>
            </Form.Group>

            <Form.Group as={Row} className="mb-3">
              <Form.Label column sm={3}>Due Date</Form.Label>
              <Col sm={9}>
                <Form.Control
                  type="date"
                  value={dueDate}
                  onChange={e => setDueDate(e.target.value)}
                />
              </Col>
            </Form.Group>

            <Form.Group className="mb-4">
              <Form.Label>Attachments</Form.Label>
              <Form.Control
                type="file"
                multiple
                onChange={e => setFiles(Array.from(e.target.files))}
              />
              {files.length > 0 && (
                <ul className="mt-2">
                  {files.map((f,i) => <li key={i}>{f.name}</li>)}
                </ul>
              )}
            </Form.Group>

            <div className="text-end">
              <Button disabled={saving} type="submit">
                {saving
                  ? <><Spinner size="sm" animation="border"/> Creating…</>
                  : 'Create Task'}
              </Button>
            </div>
          </Form>
        </Card.Body>
      </Card>
    </Container>
  )
}
