// src/pages/Login.js

const {
  REACT_APP_COGNITO_DOMAIN: DOMAIN,
  REACT_APP_CLIENT_ID:      CLIENT_ID,
  REACT_APP_API_BASE_URL:   API_BASE
} = process.env;

const SCOPE        = 'email openid phone profile';
const REDIRECT_URI = `${API_BASE}/auth/callback`;

export default function Login() {
  const loginUrl = [
    `https://eu-north-1u8wcgtv8c.auth.eu-north-1.amazoncognito.com/login/continue?client_id=6thkk9j96oa02djeccritml1gr&redirect_uri=https%3A%2F%2Fk8xh767ord.execute-api.eu-north-1.amazonaws.com%2Fyarab%2Fauth%2Fcallback&response_type=code&scope=email+openid+profile`
  ].join('');

  return (
    <div style={{ maxWidth: 400, margin: '2rem auto', textAlign: 'center' }}>
      <h2>Sign In</h2>
      <a href={loginUrl}>
        <button style={{ padding: '8px 16px', fontSize: '16px' }}>
          Login with Cognito
        </button>
      </a>
    </div>
  );
}
