// src/pages/CreateTask.jsx

import React, { useState } from 'react'
import {
  Container, Card, Form, Button,
  Row, Col, Alert, Spinner
} from 'react-bootstrap'
import api from '../api'
import { useNavigate } from 'react-router-dom'

export default function CreateTask() {
  const navigate = useNavigate()
  const userId   = '32548b14-5f37-4cf1-8928-9a5f59efdfc6'

  const [title,       setTitle]       = useState('')
  const [description, setDescription] = useState('')
  const [dueDate,     setDueDate]     = useState('')
  const [priority,    setPriority]    = useState('Medium')
  const [files,       setFiles]       = useState([])
  const [error,       setError]       = useState(null)
  const [saving,      setSaving]      = useState(false)

  const handleSubmit = async e => {
    e.preventDefault()
    setError(null)
    setSaving(true)

    try {
      // Build a FormData blob with ALL fields + files
      const formData = new FormData()
      formData.append('title',       title)
      formData.append('description', description)
      formData.append('dueDate',     dueDate)
      formData.append('priority',    priority)
      formData.append('userId',      userId)

      // append each File object under "attachments"
      files.forEach(f => formData.append('attachments', f))

      // **DO NOT** override Content-Type – Axios will auto-insert the right boundary
      const resp = await api.post('/tasks', formData)

      if (resp.status === 201) {
        navigate('/tasks')
      } else {
        throw new Error(resp.data?.error || 'Unexpected response')
      }
    } catch (err) {
      setError(err.message || 'Failed to create task')
    } finally {
      setSaving(false)
    }
  }

  return (
    <Container className="my-5">
      <Card className="mx-auto shadow-sm" style={{maxWidth:600}}>
        <Card.Header className="bg-primary text-white">
          <h3>Create New Task</h3>
        </Card.Header>
        <Card.Body>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={handleSubmit}>

            {/* Title */}
            <Form.Group as={Row} className="mb-3">
              <Form.Label column sm={3}>Title</Form.Label>
              <Col sm={9}>
                <Form.Control
                  value={title}
                  onChange={e => setTitle(e.target.value)}
                  required
                />
              </Col>
            </Form.Group>

            {/* Description */}
            <Form.Group className="mb-3">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={4}
                value={description}
                onChange={e => setDescription(e.target.value)}
              />
            </Form.Group>

            {/* Priority */}
            <Form.Group as={Row} className="mb-3">
              <Form.Label column sm={3}>Priority</Form.Label>
              <Col sm={9}>
                <Form.Select
                  value={priority}
                  onChange={e => setPriority(e.target.value)}
                >
                  <option>Low</option>
                  <option>Medium</option>
                  <option>High</option>
                </Form.Select>
              </Col>
            </Form.Group>

            {/* Due Date */}
            <Form.Group as={Row} className="mb-3">
              <Form.Label column sm={3}>Due Date</Form.Label>
              <Col sm={9}>
                <Form.Control
                  type="date"
                  value={dueDate}
                  onChange={e => setDueDate(e.target.value)}
                />
              </Col>
            </Form.Group>

            {/* Attachments */}
            <Form.Group className="mb-4">
              <Form.Label>Attachments</Form.Label>
              <Form.Control
                type="file"
                multiple
                onChange={e => setFiles(Array.from(e.target.files))}
              />
              {files.length > 0 && (
                <ul className="mt-2">
                  {files.map((f,i) => <li key={i}>{f.name}</li>)}
                </ul>
              )}
            </Form.Group>

            <div className="text-end">
              <Button disabled={saving} type="submit">
                {saving
                  ? <><Spinner size="sm" animation="border"/> Creating…</>
                  : 'Create Task'}
              </Button>
            </div>
          </Form>
        </Card.Body>
      </Card>
    </Container>
  )
}
