// src/pages/Login.js

const {
  REACT_APP_COGNITO_DOMAIN: DOMAIN,
  REACT_APP_CLIENT_ID:      CLIENT_ID,
  REACT_APP_API_BASE_URL:   API_BASE
} = process.env;

const SCOPE        = 'email openid phone profile';
const REDIRECT_URI = `${API_BASE}/auth/callback`;

export default function Login() {
  const loginUrl = [
    `https://${DOMAIN}/login`,
    `?client_id=${CLIENT_ID}`,
    `&response_type=code`,
    `&scope=${encodeURIComponent(SCOPE)}`,
    `&redirect_uri=${encodeURIComponent(REDIRECT_URI)}`
  ].join('');

  return (
    <div style={{ maxWidth: 400, margin: '2rem auto', textAlign: 'center' }}>
      <h2>Sign In</h2>
      <a href={loginUrl}>
        <button style={{ padding: '8px 16px', fontSize: '16px' }}>
          Login with Cognito
        </button>
      </a>
    </div>
  );
}
