// src/api.js

import axios from 'axios';
import Cookies from 'js-cookie';

// 1) Create an axios instance with your API base URL
const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL,  // e.g. https://your-api.execute-api.eu-north-1.amazonaws.com/pre-prod
});

// 2) On every request, read the Cognito idToken from cookies
//    and attach it as the Authorization header
api.interceptors.request.use(
  (config) => {
    const idToken = Cookies.get('idToken');
    if (idToken) {
      config.headers = {
        ...config.headers,
        Authorization: `Bearer ${idToken}`,
      };
    }
    return config;
  },
  (error) => Promise.reject(error)
);

export default api;
