// src/pages/Tasks.jsx

import React, { useEffect, useState } from 'react';
import Cookies from 'js-cookie';
import { Card, Button, Spinner, Alert, Container } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import api from '../api';

export default function Tasks() {
  const [tasks, setTasks]     = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError]     = useState(null);

  useEffect(() => {
    async function fetchTasks() {
      try {
        // ▶ 1) Grab your Cognito idToken from cookies
        const idToken = Cookies.get('idToken');
        if (!idToken) throw new Error('Not authenticated');

        // ▶ 2) Call your GET /tasks with the Bearer token
        const resp = await api.get('/tasks', {
          headers: { Authorization: `Bearer ${idToken}` }
        });
        console.log('raw tasks payload:', resp.data);

        // ▶ 3) Unwrap Lambda-proxy if present
        let payload = resp.data;
        if (
          payload &&
          typeof payload === 'object' &&
          typeof payload.statusCode === 'number'
        ) {
          if (payload.statusCode !== 200) {
            const errBody = JSON.parse(payload.body || '{}');
            throw new Error(errBody.error || 'Failed to load tasks');
          }
          payload = JSON.parse(payload.body);
        }

        // ▶ 4) Normalize into an array
        const list = Array.isArray(payload)
          ? payload
          : Array.isArray(payload.tasks)
          ? payload.tasks
          : [];

        setTasks(list);
      } catch (err) {
        console.error('Fetch tasks error:', err);
        setError(err.message || 'Failed to load tasks');
      } finally {
        setLoading(false);
      }
    }

    fetchTasks();
  }, []);

  if (loading) {
    return (
      <Container className="mt-4 text-center">
        <Spinner animation="border" />
      </Container>
    );
  }

  if (error) {
    return (
      <Container className="mt-4">
        <Alert variant="danger">
          Error loading tasks<br />
          {error}{' '}
          <Button
            variant="outline-danger"
            size="sm"
            onClick={() => {
              setLoading(true);
              setError(null);
              setTasks([]);
              // re-trigger the effect
              setTimeout(() => fetchTasks(), 0);
            }}
          >
            Retry
          </Button>
        </Alert>
      </Container>
    );
  }

  return (
    <Container className="mt-4">
      <h2>Your Tasks</h2>
      {tasks.length === 0 ? (
        <p>
          No tasks found.&nbsp;
          <Link to="/create-task">Create one now</Link>
        </p>
      ) : (
        <div className="row">
          {tasks.map(task => (
            <div key={task.taskId} className="col-md-4 mb-4">
              <Card className="h-100">
                <Card.Body>
                  <Card.Title>{task.title}</Card.Title>
                  {task.description && (
                    <Card.Text>{task.description}</Card.Text>
                  )}
                  <Card.Text>
                    <small>Status: {task.status}</small>
                  </Card.Text>
                  <Button
                    as={Link}
                    to={`/tasks/${task.taskId}`}
                    variant="primary"
                  >
                    View Details
                  </Button>
                </Card.Body>
              </Card>
            </div>
          ))}
        </div>
      )}
    </Container>
  );
}
