// src/pages/CreateTask.jsx

import React, { useState } from 'react'
import {
  Container, Card, Form, Button,
  Row, Col, Alert, Spinner
} from 'react-bootstrap'
import api from '../api'
import { useNavigate } from 'react-router-dom'

export default function CreateTask() {
  const navigate = useNavigate()

  const userId = '32548b14-5f37-4cf1-8928-9a5f59efdfc6'

  const [title, setTitle]             = useState('')
  const [description, setDescription] = useState('')
  const [dueDate, setDueDate]         = useState('')
  const [priority, setPriority]       = useState('Medium')
  const [files, setFiles]             = useState([])     // File objects
  const [error, setError]             = useState(null)
  const [saving, setSaving]           = useState(false)

  // 1️⃣ If there are files, get presigned URLs and upload each
  async function getPresignedUrl(file) {
    const resp = await api.post(
      '/tasks/attachment-url',
      { filename: file.name, fileType: file.type },
      { headers: { 'Content-Type': 'application/json' } }
    )
    return resp.data  // { uploadUrl, fileUrl }
  }

  async function uploadAllFiles() {
    const urls = []
    for (let file of files) {
      const { uploadUrl, fileUrl } = await getPresignedUrl(file)
      const put = await fetch(uploadUrl, {
        method: 'PUT',
        headers: { 'Content-Type': file.type },
        body: file
      })
      if (!put.ok) {
        throw new Error(`Failed uploading ${file.name}: ${put.status}`)
      }
      urls.push(fileUrl)
    }
    return urls
  }

  const handleSubmit = async e => {
    e.preventDefault()
    setError(null)
    setSaving(true)

    try {
      // 2️⃣ Upload attachments first, if any
      let attachments = []
      if (files.length) {
        attachments = await uploadAllFiles()
      }

      // 3️⃣ Build JSON payload (always JSON)
      const payload = {
        title,
        description,
        dueDate,
        priority,
        userId,
        attachments   // [] if none, or array of S3 URLs
      }

      // 4️⃣ Send to your Lambda→API Gateway
      const resp = await api.post('/tasks', payload)

      if (resp.status === 201) {
        navigate('/tasks')
      } else {
        throw new Error(resp.data?.error || 'Unexpected response')
      }
    } catch (err) {
      console.error(err)
      setError(err.message || 'Create failed')
    } finally {
      setSaving(false)
    }
  }

  return (
    <Container className="my-5">
      <Card className="mx-auto shadow-sm" style={{ maxWidth: '600px' }}>
        <Card.Header className="bg-primary text-white">
          <h3>Create New Task</h3>
        </Card.Header>
        <Card.Body>
          {error && <Alert variant="danger">{error}</Alert>}

          <Form onSubmit={handleSubmit}>
            {/* Title */}
            <Form.Group as={Row} className="mb-3" controlId="taskTitle">
              <Form.Label column sm={3}>Title</Form.Label>
              <Col sm={9}>
                <Form.Control
                  type="text"
                  placeholder="Enter task title"
                  value={title}
                  onChange={e => setTitle(e.target.value)}
                  required
                />
              </Col>
            </Form.Group>

            {/* Description */}
            <Form.Group className="mb-3" controlId="taskDescription">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={4}
                placeholder="Enter a description"
                value={description}
                onChange={e => setDescription(e.target.value)}
              />
            </Form.Group>

            {/* Priority */}
            <Form.Group as={Row} className="mb-3" controlId="taskPriority">
              <Form.Label column sm={3}>Priority</Form.Label>
              <Col sm={9}>
                <Form.Select
                  value={priority}
                  onChange={e => setPriority(e.target.value)}
                >
                  <option value="Low">Low</option>
                  <option value="Medium">Medium</option>
                  <option value="High">High</option>
                </Form.Select>
              </Col>
            </Form.Group>

            {/* Due Date */}
            <Form.Group as={Row} className="mb-3" controlId="taskDueDate">
              <Form.Label column sm={3}>Due Date</Form.Label>
              <Col sm={9}>
                <Form.Control
                  type="date"
                  value={dueDate}
                  onChange={e => setDueDate(e.target.value)}
                />
              </Col>
            </Form.Group>

            {/* Attachments */}
            <Form.Group className="mb-4" controlId="taskAttachments">
              <Form.Label>Attachments</Form.Label>
              <Form.Control
                type="file"
                multiple
                onChange={e => setFiles(Array.from(e.target.files))}
              />
              {files.length > 0 && (
                <ul className="mt-2">
                  {files.map((f,i) => (
                    <li key={i}>{f.name}</li>
                  ))}
                </ul>
              )}
            </Form.Group>

            <div className="text-end">
              <Button variant="primary" type="submit" disabled={saving}>
                {saving
                  ? <><Spinner size="sm" animation="border"/> Creating…</>
                  : 'Create Task'}
              </Button>
            </div>
          </Form>
        </Card.Body>
      </Card>
    </Container>
  )
}
