import React, { useEffect, useState } from 'react';
import Cookies from 'js-cookie';
import { Card, Button, Spinner, Alert, Container } from 'react-bootstrap';
import { Link, useNavigate } from 'react-router-dom';
import api from '../api';

export default function Tasks() {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTasks = async () => {
      setLoading(true);
      setError(null);

      try {
        const idToken = Cookies.get('idToken');
        const accessToken = Cookies.get('accessToken');

        if (!idToken || !accessToken) {
          throw new Error('Authentication required');
        }

        const response = await api.get('/tasks', {
          headers: { 
            Authorization: `Bearer ${idToken}`,
            // Remove debug headers that might cause CORS issues
          },
          // Ensure proper CORS handling
          withCredentials: true
        });

        // Handle response data
        let data = response.data;

        // Handle Lambda proxy response format
        if (data?.statusCode === 200 && data.body) {
          data = JSON.parse(data.body);
        }

        if (!data?.success) {
          throw new Error(data?.error || 'Invalid response format');
        }

        setTasks(data.tasks || []);

      } catch (err) {
        console.error('Fetch tasks error:', {
          message: err.message,
          response: err.response?.data,
          stack: err.stack
        });

        // Handle specific error cases
        let errorMessage = err.message;

        if (err.message.includes('Network Error')) {
          errorMessage = 'Cannot connect to server. Please check your connection.';
        } else if (err.response?.status === 401) {
          errorMessage = 'Session expired. Please log in again.';
          Cookies.remove('idToken');
          Cookies.remove('accessToken');
          navigate('/login');
        } else if (err.response?.status === 403) {
          errorMessage = 'You don\'t have permission to view these tasks.';
        }

        setError(errorMessage);
      } finally {
        setLoading(false);
      }
    };

    fetchTasks();
  }, [navigate]);


  // Helper function for safe JSON parsing
  const safeJsonParse = (str) => {
    try {
      return typeof str === 'string' ? JSON.parse(str) : str;
    } catch (e) {
      console.error('Failed to parse JSON:', str);
      return {};
    }
  };

  // Helper function to validate and normalize task data
  const validateTaskData = (data) => {
    if (!data) return [];

    // Handle array response
    if (Array.isArray(data)) {
      return data.filter(item => item.taskId);
    }

    // Handle object with tasks array
    if (data.tasks && Array.isArray(data.tasks)) {
      return data.tasks.filter(item => item.taskId);
    }

    return [];
  };

  if (loading) {
    return (
      <Container className="mt-4 text-center">
        <Spinner animation="border" role="status">
          <span className="visually-hidden">Loading...</span>
        </Spinner>
        <p className="mt-2">Loading your tasks...</p>
      </Container>
    );
  }

  if (error) {
    return (
      <Container className="mt-4">
        <Alert variant="danger" className="d-flex flex-column">
          <div className="d-flex justify-content-between align-items-center">
            <strong>Error loading tasks</strong>
            <Button 
              variant="outline-danger" 
              size="sm"
              onClick={() => window.location.reload()}
            >
              Retry
            </Button>
          </div>
          <div className="mt-2">
            <small className="text-muted">{error}</small>
          </div>
          {error.includes('authorizer') && (
            <div className="mt-2">
              <Alert variant="warning" className="p-2 mb-0">
                <small>This appears to be a server configuration issue. Our team has been notified.</small>
              </Alert>
            </div>
          )}
        </Alert>
      </Container>
    );
  }

  return (
    <Container className="mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Your Tasks</h2>
        <Button as={Link} to="/create-task" variant="primary" size="sm">
          + Create Task
        </Button>
      </div>

      {tasks.length === 0 ? (
        <Alert variant="info">
          No tasks found. <Link to="/create-task">Create your first task</Link>
        </Alert>
      ) : (
        <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 g-4">
          {tasks.map(task => (
            <div key={task.taskId} className="col">
              <Card className="h-100 shadow-sm">
                <Card.Body className="d-flex flex-column">
                  <Card.Title>{task.title || 'Untitled Task'}</Card.Title>
                  {task.description && (
                    <Card.Text className="text-muted flex-grow-1">
                      {task.description}
                    </Card.Text>
                  )}
                  <div className="d-flex justify-content-between align-items-center mt-auto">
                    <span className={`badge bg-${getStatusBadgeColor(task.status)}`}>
                      {task.status || 'unknown'}
                    </span>
                    <Button 
                      as={Link} 
                      to={`/tasks/${task.taskId}`} 
                      variant="outline-primary" 
                      size="sm"
                    >
                      Details
                    </Button>
                  </div>
                </Card.Body>
              </Card>
            </div>
          ))}
        </div>
      )}
    </Container>
  );
}

// Helper for status badge colors
const getStatusBadgeColor = (status) => {
  switch (status?.toLowerCase()) {
    case 'completed': return 'success';
    case 'in progress': return 'primary';
    case 'pending': return 'warning';
    default: return 'secondary';
  }
};
